mapper.select:
select count(*)
from four_relationships

    del_status=0
    and template_type = #{templateType}
    and content = #{content}
    and account_id = #{accountId}
    and post_ids = #{postIds}
    and dept_id = #{deptId}
    and account_name like concat('%', #{accountName},
        '%')

    and account_phone = #{accountPhone}
    and account_head = #{accountHead}
    and company_id = #{companyId}
    and del_status = #{delStatus}
    and picture = #{picture}
    and file = #{file}
    and one_store_class_id = #{oneStoreClassId}

    and two_store_class_id = #{twoStoreClassId}

    and three_store_class_id =
        #{threeStoreClassId}

    and title = #{title}
    and four_relationships_list =
        #{fourRelationshipsList}

select four_relationships_id,
template_type,
content,
account_id,
post_ids,
dept_id,
four_relationships_list,
picture,
file,
remark,
three_store_class_id,
account_name,
account_phone,
account_head,
company_id,
(select count(*) from account_like where like_type = 1 and account_id = #{accountId} and target_id =
four_relationships.four_relationships_id) as likeTag,
(select GROUP_CONCAT(account_name) from account_like where like_type = 1 and target_id =
four_relationships.four_relationships_id) as likePeople,
(SELECT GROUP_CONCAT(post_name)
FROM sys_post
WHERE FIND_IN_SET(sys_post.post_id, four_relationships.post_ids)) as postNames,
create_time,
update_time
from four_relationships

    del_status=0
    and template_type = #{templateType}
    and content = #{content}
    and account_id = #{accountId}
    and post_ids = #{postIds}
    and dept_id = #{deptId}
    and account_name like concat('%', #{accountName},
        '%')

    and account_phone = #{accountPhone}
    and account_head = #{accountHead}
    and company_id = #{companyId}
    and del_status = #{delStatus}
    and picture = #{picture}
    and file = #{file}
    and one_store_class_id = #{oneStoreClassId}

    and two_store_class_id = #{twoStoreClassId}

    and three_store_class_id =
        #{threeStoreClassId}

    and title = #{title}
    and four_relationships_list =
        #{fourRelationshipsList}

    and DATE_FORMAT(create_time,'%Y-%m-%d') = #{date}


order by create_time desc

select count(distinct account_id)
from four_relationships

    del_status=0
    and template_type = #{templateType}
    and content = #{content}
    and account_id = #{accountId}
    and post_ids = #{postIds}
    and dept_id = #{deptId}
    and account_name like concat('%', #{accountName},
        '%')

    and account_phone = #{accountPhone}
    and account_head = #{accountHead}
    and company_id = #{companyId}
    and del_status = #{delStatus}
    and picture = #{picture}
    and file = #{file}
    and one_store_class_id = #{oneStoreClassId}

    and two_store_class_id = #{twoStoreClassId}

    and three_store_class_id =
        #{threeStoreClassId}

    and title = #{title}
    and four_relationships_list =
        #{fourRelationshipsList}

select r.four_relationships_id ,
r.template_type ,
r.content,
r.account_id ,
r.post_ids ,
r.dept_id ,
r.four_relationships_list,
r.picture,
r.file,
r.remark,
r.create_time,
r.account_name ,
r.account_phone ,
r.account_head ,
r.company_id ,
(SELECT class_name from four_relationships_class where r.two_store_class_id=four_relationships_class_id limit 1 ) as
className,
(SELECT GROUP_CONCAT(post_name)
FROM sys_post
WHERE FIND_IN_SET(sys_post.post_id, r.post_ids)) as postNames,
r.create_time as createTime,
r.update_time as updateTime
from four_relationships r left join sys_dept d on r.dept_id = d.dept_id

    r.del_status = 0
    and r.template_type = #{templateType}
    and r.content = #{content}
    and r.account_id = #{accountId}
    and r.post_ids = #{postIds}
    and r.dept_id = #{deptId}
    and r.account_name like concat('%', #{accountName},
        '%')

    and r.account_phone = #{accountPhone}
    and r.account_head = #{accountHead}
    and r.company_id = #{companyId}
    and r.del_status = #{delStatus}
    and r.picture = #{picture}
    and r.file = #{file}
    and r.one_store_class_id = #{oneStoreClassId}

    and r.two_store_class_id = #{twoStoreClassId}

    and r.three_store_class_id =
        #{threeStoreClassId}

    and r.title = #{title}
    and r.four_relationships_list =
        #{fourRelationshipsList}

    and date_format(r.create_time,'%Y-%m-%d')
         >=  #{startTime}

    and date_format(r.create_time,'%Y-%m-%d')
         <=  #{endTime}



${params.dataScope}
order by r.create_time desc

mapper.update:
update four_relationships
template_type = #{templateType}, content = #{content}, account_id = #{accountId}, post_ids = #{postIds}, dept_id = #{deptId}, account_name = #{accountName}, account_phone = #{accountPhone}, account_head = #{accountHead}, company_id = #{companyId}, del_status = #{delStatus}, create_time = #{createTime}, update_time = #{updateTime}, picture = #{picture}, remark = #{remark}, file = #{file}, one_store_class_id = #{oneStoreClassId}, two_store_class_id = #{twoStoreClassId}, three_store_class_id = #{threeStoreClassId}, title = #{title}, four_relationships_list = #{fourRelationshipsList},
where four_relationships_id = #{fourRelationshipsId}
